#include <iostream>

using namespace std;

int n, m, cnt;
pair<int, int> a;
pair<int, int> ans;
char field[1000][1000];

void travel(int x, int y) {
    cnt++;

    if (cnt > n * m) {
        return;
    }

    if (field[x][y] == 'k') {
        a.first++;
    }

    if (field[x][y] == 'v') {
        a.second++;
    }

    field[x][y] = '#';

    if (x - 1 >= 0) {
        if (field[x - 1][y] != '#') {
            travel(x - 1, y);
        }
    }

    if (x + 1 < n) {
        if (field[x + 1][y] != '#') {
            travel(x + 1, y);
        }
    }

    if (y - 1 >= 0) {
        if (field[x][y - 1] != '#') {
            travel(x, y - 1);
        }
    }

    if (y + 1 < m) {
        if (field[x][y + 1] != '#') {
            travel(x, y + 1);
        }
    }
}

int main() {
    cin >> n >> m;

    for (int i = 0; i < n; i++) {
            cin >> field[i];
    }

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (field[i][j] != '#') {
                a.first = 0;
                a.second = 0;
                cnt = 0;

                travel(i, j);

                if (a.first > a.second) {
                    ans.first += a.first;
                }
                else {
                    ans.second += a.second;
                }
            }
        }
    }

    cout << ans.first << " " << ans.second << endl;

    return 0;
}
